udes(lowerQuery) ? 1 : 0; const genreScore = musician.genre.toLowerCase().startsWith(lowerQuery) ? 2 : musician.genre.toLowerCase().includes(lowerQuery) ? 1 : 0; const locationScore = fuzzball.partial_ratio(musician.location.toLowerCase(), normalizedLocation); // Compare normalized location name return nameScore + genreScore + locationScore > 0 && (role === '' || musician.role === role); }).sort((a, b) => { const aNameScore = a.name.toLowerCase().startsWith(lowerQuery) ? 2 : a.name.toLowerCase().includes(lowerQuery) ? 1 : 0; const bNameScore = b.name.toLowerCase().startsWith(lowerQuery) ? 2 : b.name.toLowerCase().includes(lowerQuery) ? 1 : 0; const aGenreScore = a.genre.toLowerCase().startsWith(lowerQuery) ? 2 : a.genre.toLowerCase().includes(lowerQuery) ? 1 : 0; const bGenreScore = b.genre.toLowerCase().startsWith(lowerQuery) ? 2 : b.genre.toLowerCase().includes(lowerQuery) ? 1 : 0; const aLocationScore = fuzzball.partial_ratio(a.location.toLowerCase(), normalizedLocation); // Compare normalized location name const bLocationScore = fuzzball.partial_ratio(b.location.toLowerCase(), normalizedLocation); // Compare normalized location name return (bNameScore + bGenreScore + bLocationScore) - (aNameScore + aGenreScore + aLocationScore); }); } return results; } app.use((req, res, next) => { if (req.session.musicianId) { const musician = getMusicianById(req.session.musicianId); res.locals.musician = musician; res.locals.userLoggedIn = true; res.locals.username = musician.name; } else { res.locals.userLoggedIn = false; } next(); }); app.get(\\"/\\", (req, res) => { const data = fs.readFileSync(\\"./db/musicians.json\\"); const musicians = JSON.parse(data); res.render(\\"index\\", { musicians: musicians.musicians }); }); app.get(\\"/register\\", (req, res) => { if (req.session.musicianId) { const musician = getMusicianById(req.session.musicianId); res.redirect(\\"/profile/\\" + musician.id); } else { res.render(\\"register\\"); } }); app.post(\\"/register\\", (req, res) => 