has[i] = nullptr; // Correção: Defina o ponteiro como nullptr após a exclusão } delete[] vetorLinhas; } void SparseMatrix::inserir(int i, int j, double valor) { if (i < 0 || i >= linhas || j < 0 || j >= colunas) { std::cout << \\"Célula inválida na matriz: (\\" << i << \\", \\" << j << \\")\\" << std::endl; return; } Node *novoNo = new Node(i, j, valor); novoNo->proximo = vetorLinhas[i]; vetorLinhas[i] = novoNo; } double SparseMatrix::get(int i, int j) { if (i < 0 || i >= linhas || j < 0 || j >= colunas) { std::cout << \\"Célula inválida na matriz: (\\" << i << \\", \\" << j << \\")\\" << std::endl; return 0.0; } Node *atual = vetorLinhas[i]; while (atual != nullptr) { if (atual->coluna == j) { return atual->valor; } atual = atual->proximo; } return 0.0; // Valor padrão para células não encontradas } void SparseMatrix::imprimir() { for (int i = 0; i < linhas; i++) { for (int j = 0; j < colunas; j++) { double valor = get(i, j); std::cout << valor << \\" \\"; } std::cout << std::endl; } }","output":"Para implementar o código sem utilizar vetores, você pode fazer uso da estrutura de dados de lista ligada duplamente com cabeça de lista nas linhas e colunas, como mostrado abaixo: \`\`\`cpp #include <iostream> #include \\"SparseMatrix.h\\" SparseMatrix::SparseMatrix(int m, int n) { if (m <= 0 || n <= 0) { std::cout << \\"Dimensões inválidas para a matriz: \\" << m << \\" x \\" << n << std::endl; linhas = 0; colunas = 0; return; } linhas = m; colunas = n; head = new Node(-1, -1, 0.0); head->nextInRow = head; head->nextInCol = head; } SparseMatrix::~SparseMatrix() { Node *rowNode = head->nextInRow; while (rowNode != head) { Node *colNode = rowNode->nextInCol; while (colNode != rowNode) { Node *next = colNode->nextInCol; delete colNode; colNode = next; } rowNode = rowNode->nextInRow; } delete head; } void SparseMatrix::inserir(int i, int j, double valor) { if (i < 0 || i >= linhas || j < 0 || j >= colunas) { std::cout << \\"Célula inválida na matriz: (\\" << i << \\", \\" << j << \\")\\" << std::endl; return; } Node *novoNo = new Node(i, j, valor); Node *rowNode = findNodeInRow(i); if (rowNode->row != i) // Se não encontrarmos um nó com a mesma linha, insira a nova linha { novoNo->nextInRow = rowNode->nextInRow; rowNode->nextInRow = novoNo; } Node *colNode = findNodeInCol(j); if (colNode->col != j) // Se não encontrarmos um nó com a mesma coluna, insira a nova coluna { novoNo->nextInCol = colNode->nextInCol; colNode->nextInCol = novoNo; } } SparseMatrix::Node *SparseMatrix::findNodeInRow(int row) { Node *n = head->nextInRow; while (n != head && n->row < row) { n = n->nextInRow; } return n; } SparseMatrix::Node *SparseMatrix::findNodeInCol(int col) { Node *n = head->nextInCol; while (n != head && n->col < col) { n = n->nextInCol; } return n; } double SparseMatrix::get(int i, int j) { if (i < 0 || i >= linhas || j < 0 || j >= colunas) { std::cout << \\"Célula inválida na matriz: (\\" << i << \\", \\" << j << \\")\\" << std::endl; return 0.0; } Node *rowNode = findNodeInRow(i); if (rowNode->row != i) { return 0.0; // Valor padrão para células não encontradas } Node *colNode = findNodeInCol(j); if (colNode->col != j) { return 0.0; // Valor padrão para células não encontradas } return colNode->valor; } void SparseMatrix::imprimir() { Node *rowNode = head->nextInRow; while (rowNode != head) { Node *colNode = rowNode->nextInCol; for (int j = 0; j < colunas; j++) { if (colNode->col == j) { std::cout << colNode->valor << \\" \\"; colNode = colNode->nextInCol; } else { std::cout << \\"0.0 \\"; } } std::cout << std::endl; } } \`\`\` Nesse código, a matriz esparsa é representada por uma estrutura de dados baseada em lista ligada duplamente com cabeças de lista para linhas e colunas. O método \`findNodeInRow\` encontra o nó com a linha especificada ou retorna o nó imediatamente antes de onde a linha deveria ser inserida. O mesmo se aplica ao método \`findNodeInCol\` para colunas."},{"instruction":"(I managed to escape from those Pillagers this time