///////////////////////////////////////////////////////// // CONSTANTS // ////////////////////////////////////////////////////////////// ISuperRegistry public immutable superRegistry; uint64 public immutable CHAIN_ID; uint32 private constant TIMELOCK_FORM_ID = 2; ////////////////////////////////////////////////////////////// // STATE VARIABLES // ////////////////////////////////////////////////////////////// /// @dev xchain params mapping(uint64 chainId => AggregatorV3Interface) public nativeFeedOracle; mapping(uint64 chainId => AggregatorV3Interface) public gasPriceOracle; mapping(uint64 chainId => uint256 gasForSwap) public swapGasUsed; mapping(uint64 chainId => uint256 gasForUpdate) public updateGasUsed; mapping(uint64 chainId => uint256 gasForOps) public depositGasUsed; mapping(uint64 chainId => uint256 gasForOps) public withdrawGasUsed; mapping(uint64 chainId => uint256 defaultNativePrice) public nativePrice; mapping(uint64 chainId => uint256 defaultGasPrice) public gasPrice; mapping(uint64 chainId => uint256 gasPerByte) public gasPerByte; mapping(uint64 chainId => uint256 gasForOps) public ackGasCost; mapping(uint64 chainId => uint256 gasForOps) public timelockCost; /// @dev register transmuter params uint256 public totalTransmuterFees; bytes public extraDataForTransmuter; ////////////////////////////////////////////////////////////// // STRUCTS // ////////////////////////////////////////////////////////////// struct EstimateAckCostVars { uint256 currPayloadId; uint256 payloadHeader; uint8 callbackType; bytes payloadBody; uint8[] ackAmbIds; uint8 isMulti; uint64 srcChainId; bytes message; } ////////////////////////////////////////////////////////////// // MODIFIERS // ////////////////////////////////////////////////////////////// modifier onlyProtocolAdmin() { if (!ISuperRBAC(superRegistry.getAddress(keccak256(\\"SUPER_RBAC\\"))).hasProtocolAdminRole(msg.sender)) { revert Error.NOT_PROTOCOL_ADMIN(); } _; } modifier onlyEmergencyAdmin() { if (!ISuperRBAC(superRegistry.getAddress(keccak256(\\"SUPER_RBAC\\"))).hasEmergencyAdminRole(msg.sender)) { revert Error.NOT_EMERGENCY_ADMIN(); } _; } ////////////////////////////////////////////////////////////// // CONSTRUCTOR // ////////////////////////////////////////////////////////////// constructor(address superRegistry_) { if (block.chainid > type(uint64).max) { revert Error.BLOCK_CHAIN_ID_OUT_OF_BOUNDS(); } CHAIN_ID = uint64(block.chainid); superRegistry = ISuperRegistry(superRegistry_); } ////////////////////////////////////////////////////////////// // EXTERNAL VIEW FUNCTIONS // ////////////////////////////////////////////////////////////// /// @inheritdoc IPaymentHelper function calculateAMBData( uint64 dstChainId_, uint8[] calldata ambIds_, bytes memory message_ ) external view override returns (uint256 totalFees, bytes memory extraData) { (uint256[] memory gasPerAMB, bytes[] memory extraDataPerAMB, uint256 fees) = _estimateAMBFeesReturnExtraData(dstChainId_, ambIds_, message_); extraData = abi.encode(AMBExtraData(gasPerAMB, extraDataPerAMB)); totalFees = fees; } /// @inheritdoc IPaymentHelper function getRegisterTransmuterAMBData() external view override returns (uint256 totalFees, bytes memory extraData) { return (totalTransmuterFees, extraDataForTransmuter); } /// @inheritdoc IPaymentHelper function estimateMultiDstMultiVault( MultiDstMultiVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 dstAmount, uint256 totalAmount) { uint256 len = req_.dstChainIds.length; uint256 superformIdsLen; uint256 totalDstGas; for (uint256 i; i < len; ++i) { totalDstGas = 0; /// @dev step 1: estimate amb costs uint256 ambFees = _estimateAMBFees( req_.ambIds[i], req_.dstChainIds[i], _generateMultiVaultMessage(req_.superformsData[i]) ); superformIdsLen = req_.superformsData[i].superformIds.length; srcAmount += ambFees; if (isDeposit_) { /// @dev step 2: estimate update cost (only for deposit) totalDstGas += _estimateUpdateCost(req_.dstChainIds[i], superformIdsLen); /// @dev step 3: estimation processing cost of acknowledgement /// @notice optimistically estimating. (Ideal case scenario: no failed deposits / withdrawals) srcAmount += _estimateAckProcessingCost(superformIdsLen); /// @dev step 4: estimate liq amount liqAmount += _estimateLiqAmount(req_.superformsData[i].liqRequests); /// @dev step 5: estimate dst swap cost if it exists totalDstGas += _estimateSwapFees(req_.dstChainIds[i], req_.superformsData[i].hasDstSwaps); } /// @dev step 6: estimate execution costs in dst (withdraw / deposit) /// note: execution cost includes acknowledgement messaging cost totalDstGas += _estimateDstExecutionCost(isDeposit_, req_.dstChainIds[i], superformIdsLen); /// @dev step 6: estimate if timelock form processing costs are involved if (!isDeposit_) { for (uint256 j; j < superformIdsLen; ++j) { (, uint32 formId,) = req_.superformsData[i].superformIds[j].getSuperform(); if (formId == TIMELOCK_FORM_ID) { totalDstGas += timelockCost[req_.dstChainIds[i]]; } } } /// @dev step 7: convert all dst gas estimates to src chain estimate (withdraw / deposit) dstAmount += _convertToNativeFee(req_.dstChainIds[i], totalDstGas); } totalAmount = srcAmount + dstAmount + liqAmount; } /// @inheritdoc IPaymentHelper function estimateMultiDstSingleVault( MultiDstSingleVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 dstAmount, uint256 totalAmount) { uint256 len = req_.dstChainIds.length; for (uint256 i; i < len; ++i) { uint256 totalDstGas; /// @dev step 1: estimate amb costs uint256 ambFees = _estimateAMBFees( req_.ambIds[i], req_.dstChainIds[i], _generateSingleVaultMessage(req_.superformsData[i]) ); srcAmount += ambFees; if (isDeposit_) { /// @dev step 2: estimate update cost (only for deposit) totalDstGas += _estimateUpdateCost(req_.dstChainIds[i], 1); /// @dev step 3: estimation execution cost of acknowledgement srcAmount += _estimateAckProcessingCost(1); /// @dev step 4: estimate the liqAmount liqAmount += _estimateLiqAmount(req_.superformsData[i].liqRequest.castLiqRequestToArray()); /// @dev step 5: estimate if swap costs are involved totalDstGas += _estimateSwapFees(req_.dstChainIds[i], req_.superformsData[i].hasDstSwap.castBoolToArray()); } /// @dev step 5: estimate execution costs in dst /// note: execution cost includes acknowledgement messaging cost totalDstGas += _estimateDstExecutionCost(isDeposit_, req_.dstChainIds[i], 1); /// @dev step 6: estimate if timelock form processing costs are involved (, uint32 formId,) = req_.superformsData[i].superformId.getSuperform(); if (!isDeposit_ && formId == TIMELOCK_FORM_ID) { totalDstGas += timelockCost[req_.dstChainIds[i]]; } /// @dev step 7: convert all dst gas estimates to src chain estimate dstAmount += _convertToNativeFee(req_.dstChainIds[i], totalDstGas); } totalAmount = srcAmount + dstAmount + liqAmount; } /// @inheritdoc IPaymentHelper function estimateSingleXChainMultiVault( SingleXChainMultiVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 dstAmount, uint256 totalAmount) { uint256 totalDstGas; uint256 superformIdsLen = req_.superformsData.superformIds.length; /// @dev step 1: estimate amb costs uint256 ambFees = _estimateAMBFees(req_.ambIds, req_.dstChainId, _generateMultiVaultMessage(req_.superformsData)); srcAmount += ambFees; /// @dev step 2: estimate update cost (only for deposit) if (isDeposit_) totalDstGas += _estimateUpdateCost(req_.dstChainId, superformIdsLen); /// @dev step 3: estimate execution costs in dst /// note: execution cost includes acknowledgement messaging cost totalDstGas += _estimateDstExecutionCost(isDeposit_, req_.dstChainId, superformIdsLen); /// @dev step 4: estimation execution cost of acknowledgement if (isDeposit_) srcAmount += _estimateAckProcessingCost(superformIdsLen); /// @dev step 5: estimate liq amount if (isDeposit_) liqAmount += _estimateLiqAmount(req_.superformsData.liqRequests); /// @dev step 6: estimate if swap costs are involved if (isDeposit_) totalDstGas += _estimateSwapFees(req_.dstChainId, req_.superformsData.hasDstSwaps); /// @dev step 7: estimate if timelock form processing costs are involved if (!isDeposit_) { for (uint256 i; i < superformIdsLen; ++i) { (, uint32 formId,) = req_.superformsData.superformIds[i].getSuperform(); if (formId == TIMELOCK_FORM_ID) { totalDstGas += timelockCost[CHAIN_ID]; } } } /// @dev step 8: convert all dst gas estimates to src chain estimate dstAmount += _convertToNativeFee(req_.dstChainId, totalDstGas); totalAmount = srcAmount + dstAmount + liqAmount; } /// @inheritdoc IPaymentHelper function estimateSingleXChainSingleVault( SingleXChainSingleVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 dstAmount, uint256 totalAmount) { uint256 totalDstGas; /// @dev step 1: estimate amb costs uint256 ambFees = _estimateAMBFees(req_.ambIds, req_.dstChainId, _generateSingleVaultMessage(req_.superformData)); srcAmount += ambFees; /// @dev step 2: estimate update cost (only for deposit) if (isDeposit_) totalDstGas += _estimateUpdateCost(req_.dstChainId, 1); /// @dev step 3: estimate execution costs in dst /// note: execution cost includes acknowledgement messaging cost totalDstGas += _estimateDstExecutionCost(isDeposit_, req_.dstChainId, 1); /// @dev step 4: estimation execution cost of acknowledgement if (isDeposit_) srcAmount += _estimateAckProcessingCost(1); /// @dev step 5: estimate the liq amount if (isDeposit_) liqAmount += _estimateLiqAmount(req_.superformData.liqRequest.castLiqRequestToArray()); /// @dev step 6: estimate if swap costs are involved if (isDeposit_) { totalDstGas += _estimateSwapFees(req_.dstChainId, req_.superformData.hasDstSwap.castBoolToArray()); } /// @dev step 7: estimate if timelock form processing costs are involved (, uint32 formId,) = req_.superformData.superformId.getSuperform(); if (!isDeposit_ && formId == TIMELOCK_FORM_ID) { totalDstGas += timelockCost[CHAIN_ID]; } /// @dev step 8: convert all dst gas estimates to src chain estimate dstAmount += _convertToNativeFee(req_.dstChainId, totalDstGas); totalAmount = srcAmount + dstAmount + liqAmount; } /// @inheritdoc IPaymentHelper function estimateSingleDirectSingleVault( SingleDirectSingleVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 totalAmount) { (, uint32 formId,) = req_.superformData.superformId.getSuperform(); /// @dev only if timelock form withdrawal is involved if (!isDeposit_ && formId == TIMELOCK_FORM_ID) { srcAmount += timelockCost[CHAIN_ID] * _getGasPrice(CHAIN_ID); } if (isDeposit_) liqAmount += _estimateLiqAmount(req_.superformData.liqRequest.castLiqRequestToArray()); /// @dev not adding dstAmount to save some GAS totalAmount = liqAmount + srcAmount; } /// @inheritdoc IPaymentHelper function estimateSingleDirectMultiVault( SingleDirectMultiVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 totalAmount) { uint256 len = req_.superformData.superformIds.length; for (uint256 i; i < len; ++i) { (, uint32 formId,) = req_.superformData.superformIds[i].getSuperform(); uint256 timelockPrice = timelockCost[uint64(block.chainid)] * _getGasPrice(uint64(block.chainid)); /// @dev only if timelock form withdrawal is involved if (!isDeposit_ && formId == TIMELOCK_FORM_ID) { srcAmount += timelockPrice; } } if (isDeposit_) liqAmount += _estimateLiqAmount(req_.superformData.liqRequests); /// @dev not adding dstAmount to save some GAS totalAmount = liqAmount + srcAmount; } /// @inheritdoc IPaymentHelper function estimateAMBFees( uint8[] memory ambIds_, uint64 dstChainId_, bytes memory message_, bytes[] memory extraData_ ) public view returns (uint256 totalFees, uint256[] memory) { uint256 len = ambIds_.length; uint256[] memory fees = new uint256[](len); /// @dev just checks the estimate for sending message from src -> dst for (uint256 i; i < len; ++i) { fees[i] = CHAIN_ID != dstChainId_ ? IAmbImplementation(superRegistry.getAmbAddress(ambIds_[i])).estimateFees( dstChainId_, message_, extraData_[i] ) : 0; totalFees += fees[i]; } return (totalFees, fees); } ////////////////////////////////////////////////////////////// // EXTERNAL WRITE FUNCTIONS // ////////////////////////////////////////////////////////////// /// @inheritdoc IPaymentHelper function addRemoteChain( uint64 chainId_, PaymentHelperConfig calldata config_ ) external override onlyProtocolAdmin { if (config_.nativeFeedOracle != address(0)) { nativeFeedOracle[chainId_] = AggregatorV3Interface(config_.nativeFeedOracle); } if (config_.gasPriceOracle != address(0)) { gasPriceOracle[chainId_] = AggregatorV3Interface(config_.gasPriceOracle); } swapGasUsed[chainId_] = config_.swapGasUsed; updateGasUsed[chainId_] = config_.updateGasUsed; depositGasUsed[chainId_] = config_.depositGasUsed; withdrawGasUsed[chainId_] = config_.withdrawGasUsed; nativePrice[chainId_] = config_.defaultNativePrice; gasPrice[chainId_] = config_.defaultGasPrice; gasPerByte[chainId_] = config_.dstGasPerByte; ackGasCost[chainId_] = config_.ackGasCost; timelockCost[chainId_] = config_.timelockCost; } /// @inheritdoc IPaymentHelper function updateRemoteChain( uint64 chainId_, uint256 configType_, bytes memory config_ ) external override onlyEmergencyAdmin { /// @dev Type 1: DST TOKEN PRICE FEED ORACLE if (configType_ == 1) { nativeFeedOracle[chainId_] = AggregatorV3Interface(abi.decode(config_, (address))); } /// @dev Type 2: DST GAS PRICE ORACLE if (configType_ == 2) { gasPriceOracle[chainId_] = AggregatorV3Interface(abi.decode(config_, (address))); } /// @dev Type 3: SWAP GAS USED if (configType_ == 3) { swapGasUsed[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 4: PAYLOAD UPDATE GAS COST PER TX FOR DEPOSIT if (configType_ == 4) { updateGasUsed[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 5: DEPOSIT GAS COST PER TX if (configType_ == 5) { depositGasUsed[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 6: WITHDRAW GAS COST PER TX if (configType_ == 6) { withdrawGasUsed[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 7: DEFAULT NATIVE PRICE if (configType_ == 7) { nativePrice[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 8: DEFAULT GAS PRICE if (configType_ == 8) { gasPrice[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 9: GAS PRICE PER Byte of Message if (configType_ == 9) { gasPerByte[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 10: ACK GAS COST if (configType_ == 10) { ackGasCost[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 11: TIMELOCK PROCESSING COST if (configType_ == 11) { timelockCost[chainId_] = abi.decode(config_, (uint256)); } emit ChainConfigUpdated(chainId_, configType_, config_); } /// @inheritdoc IPaymentHelper function updateRegisterAERC20Params( uint256 totalTransmuterFees_, bytes memory extraDataForTransmuter_ ) external onlyEmergencyAdmin { totalTransmuterFees = totalTransmuterFees_; extraDataForTransmuter = extraDataForTransmuter_; } ////////////////////////////////////////////////////////////// // INTERNAL FUNCTIONS // ////////////////////////////////////////////////////////////// /// @dev helps generate extra data per amb function _generateExtraData( uint64 dstChainId_, uint8[] memory ambIds_, bytes memory message_ ) internal view returns (bytes[] memory extraDataPerAMB) { AMBMessage memory ambIdEncodedMessage = abi.decode(message_, (AMBMessage)); ambIdEncodedMessage.params = abi.encode(ambIds_, ambIdEncodedMessage.params); uint256 len = ambIds_.length; uint256 gasReqPerByte = gasPerByte[dstChainId_]; uint256 totalDstGasReqInWei = abi.encode(ambIdEncodedMessage).length * gasReqPerByte; AMBMessage memory decodedMessage = abi.decode(message_, (AMBMessage)); decodedMessage.params = message_.computeProofBytes(); uint256 totalDstGasReqInWeiForProof = abi.encode(decodedMessage).length * gasReqPerByte; extraDataPerAMB = new bytes[](len); for (uint256 i; i < len; ++i) { uint256 gasReq = i != 0 ? totalDstGasReqInWeiForProof : totalDstGasReqInWei; /// @dev amb id 1: layerzero /// @dev amb id 2: hyperlane /// @dev amb id 3: wormhole /// @notice id 1: encoded layerzero adapter params (version 2). Other values are not used atm. /// @notice id 2: encoded dst gas limit /// @notice id 3: encoded dst gas limit if (ambIds_[i] == 1) { extraDataPerAMB[i] = abi.encodePacked(uint16(2), gasReq, uint256(0), address(0)); } else if (ambIds_[i] == 2) { extraDataPerAMB[i] = abi.encode(gasReq); } else if (ambIds_[i] == 3) { extraDataPerAMB[i] = abi.encode(0, gasReq); } } } /// @dev helps estimate the acknowledgement costs for amb processing function estimateAckCost(uint256 payloadId_) external view returns (uint256 totalFees) { EstimateAckCostVars memory v; IBaseStateRegistry coreStateRegistry = IBaseStateRegistry(superRegistry.getAddress(keccak256(\\"CORE_STATE_REGISTRY\\"))); v.currPayloadId = coreStateRegistry.payloadsCount(); if (payloadId_ > v.currPayloadId) revert Error.INVALID_PAYLOAD_ID(); v.payloadHeader = coreStateRegistry.payloadHeader(payloadId_); v.payloadBody = coreStateRegistry.payloadBody(payloadId_); (, v.callbackType, v.isMulti,,, v.srcChainId) = DataLib.decodeTxInfo(v.payloadHeader); /// if callback type is return then return 0 if (v.callbackType != 0) return 0; if (v.isMulti == 1) { InitMultiVaultData memory data = abi.decode(v.payloadBody, (InitMultiVaultData)); v.payloadBody = abi.encode(ReturnMultiData(v.currPayloadId, data.superformIds, data.amounts)); } else { InitSingleVaultData memory data = abi.decode(v.payloadBody, (InitSingleVaultData)); v.payloadBody = abi.encode(ReturnSingleData(v.currPayloadId, data.superformId, data.amount)); } v.ackAmbIds = coreStateRegistry.getMessageAMB(payloadId_); v.message = abi.encode(AMBMessage(coreStateRegistry.payloadHeader(payloadId_), v.payloadBody)); return _estimateAMBFees(v.ackAmbIds, v.srcChainId, v.message); } /// @dev helps estimate the cross-chain message costs function _estimateAMBFees( uint8[] memory ambIds_, uint64 dstChainId_, bytes memory message_ ) internal view returns (uint256 totalFees) { uint256 len = ambIds_.length; bytes[] memory extraDataPerAMB = _generateExtraData(dstChainId_, ambIds_, message_); AMBMessage memory ambIdEncodedMessage = abi.decode(message_, (AMBMessage)); ambIdEncodedMessage.params = abi.encode(ambIds_, ambIdEncodedMessage.params); bytes memory proof_ = abi.encode(AMBMessage(type(uint256).max, abi.encode(keccak256(message_)))); /// @dev just checks the estimate for sending message from src -> dst /// @dev only ambIds_[0] = primary amb (rest of the ambs send only the proof) for (uint256 i; i < len; ++i) { uint256 tempFee = CHAIN_ID != dstChainId_ ? IAmbImplementation(superRegistry.getAmbAddress(ambIds_[i])).estimateFees( dstChainId_, i != 0 ? proof_ : abi.encode(ambIdEncodedMessage), extraDataPerAMB[i] ) : 0; totalFees += tempFee; } } /// @dev helps estimate the cross-chain message costs function _estimateAMBFeesReturnExtraData( uint64 dstChainId_, uint8[] calldata ambIds_, bytes memory message_ ) internal view returns (uint256[] memory feeSplitUp, bytes[] memory extraDataPerAMB, uint256 totalFees) { AMBMessage memory ambIdEncodedMessage = abi.decode(message_, (AMBMessage)); ambIdEncodedMessage.params = abi.encode(ambIds_, ambIdEncodedMessage.params); uint256 len = ambIds_.length; extraDataPerAMB = _generateExtraData(dstChainId_, ambIds_, message_); feeSplitUp = new uint256[](len); bytes memory proof_ = abi.encode(AMBMessage(type(uint256).max, abi.encode(keccak256(message_)))); /// @dev just checks the estimate for sending message from src -> dst for (uint256 i; i < len; ++i) { uint256 tempFee = CHAIN_ID != dstChainId_ ? IAmbImplementation(superRegistry.getAmbAddress(ambIds_[i])).estimateFees( dstChainId_, i != 0 ? proof_ : abi.encode(ambIdEncodedMessage), extraDataPerAMB[i] ) : 0; totalFees += tempFee; feeSplitUp[i] = tempFee; } } /// @dev helps estimate the liq amount involved in the tx function _estimateLiqAmount(LiqRequest[] memory req_) internal pure returns (uint256 liqAmount) { uint256 len = req_.length; for (uint256 i; i < len; ++i) { liqAmount += req_[i].nativeAmount; } } /// @dev helps estimate the dst chain swap gas limit (if multi-tx is involved) function _estimateSwapFees( uint64 dstChainId_, bool[] memory hasDstSwaps_ ) internal view returns (uint256 gasUsed) { uint256 totalSwaps; if (CHAIN_ID == dstChainId_) { return 0; } uint256 len = hasDstSwaps_.length; for (uint256 i; i < len; ++i) { /// @dev checks if hasDstSwap is true if (hasDstSwaps_[i]) { ++totalSwaps; } } if (totalSwaps == 0) { return 0; } return totalSwaps * swapGasUsed[dstChainId_]; } /// @dev helps estimate the dst chain update payload gas limit function _estimateUpdateCost(uint64 dstChainId_, uint256 vaultsCount_) internal view returns (uint256 gasUsed) { return vaultsCount_ * updateGasUsed[dstChainId_]; } /// @dev helps estimate the dst chain processing gas limit function _estimateDstExecutionCost( bool isDeposit_, uint64 dstChainId_, uint256 vaultsCount_ ) internal view returns (uint256 gasUsed) { uint256 executionGasPerVault = isDeposit_ ? depositGasUsed[dstChainId_] : withdrawGasUsed[dstChainId_]; return executionGasPerVault * vaultsCount_; } /// @dev helps estimate the src chain processing fee function _estimateAckProcessingCost(uint256 vaultsCount_) internal view returns (uint256 nativeFee) { uint256 gasCost = vaultsCount_ * ackGasCost[CHAIN_ID]; return gasCost * _getGasPrice(CHAIN_ID); } /// @dev generates the amb message for single vault data function _generateSingleVaultMessage(SingleVaultSFData memory sfData_) internal view returns (bytes memory message_) { bytes memory ambData = abi.encode( InitSingleVaultData( _getNextPayloadId(), sfData_.superformId, sfData_.amount, sfData_.maxSlippage, sfData_.liqRequest, sfData_.hasDstSwap, sfData_.retain4626, sfData_.receiverAddress, sfData_.extraFormData ) ); message_ = abi.encode(AMBMessage(type(uint256).max, ambData)); } /// @dev generates the amb message for multi vault data function _generateMultiVaultMessage(MultiVaultSFData memory sfData_) internal view returns (bytes memory message_) { bytes memory ambData = abi.encode( InitMultiVaultData( _getNextPayloadId(), sfData_.superformIds, sfData_.amounts, sfData_.maxSlippages, sfData_.liqRequests, sfData_.hasDstSwaps, sfData_.retain4626s, sfData_.receiverAddress, sfData_.extraFormData ) ); message_ = abi.encode(AMBMessage(type(uint256).max, ambData)); } /// @dev helps convert the dst gas fee into src chain native fee /// @dev https://docs.soliditylang.org/en/v0.8.4/units-and-global-variables.html#ether-units /// @dev all native tokens should be 18 decimals across all EVMs function _convertToNativeFee(uint64 dstChainId_, uint256 dstGas_) internal view returns (uint256 nativeFee) { /// @dev gas fee * gas price (to get the gas amounts in dst chain's native token) /// @dev gas price is 9 decimal (in gwei) /// @dev assumption: all evm native tokens are 18 decimals uint256 dstNativeFee = dstGas_ * _getGasPrice(dstChainId_); if (dstNativeFee == 0) { return 0; } /// @dev converts the gas to pay in terms of native token to usd value /// @dev native token price is 8 decimal uint256 dstUsdValue = dstNativeFee * _getNativeTokenPrice(dstChainId_); // native token price - 8 decimal if (dstUsdValue == 0) { return 0; } /// @dev converts the usd value to source chain's native token /// @dev native token price is 8 decimal which cancels the 8 decimal multiplied in previous step nativeFee = (dstUsdValue) / _getNativeTokenPrice(CHAIN_ID); } /// @dev helps generate the new payload id /// @dev next payload id = current payload id + 1 function _getNextPayloadId() internal view returns (uint256 nextPayloadId) { nextPayloadId = ReadOnlyBaseRegistry(superRegistry.getAddress(keccak256(\\"CORE_STATE_REGISTRY\\"))).payloadsCount(); ++nextPayloadId; } /// @dev helps return the current gas price of different networks /// @return native token price function _getGasPrice(uint64 chainId_) internal view returns (uint256) { address oracleAddr = address(gasPriceOracle[chainId_]); if (oracleAddr != address(0)) { (, int256 value,, uint256 updatedAt,) = AggregatorV3Interface(oracleAddr).latestRoundData(); if (value <= 0) revert Error.CHAINLINK_MALFUNCTION(); if (updatedAt == 0) revert Error.CHAINLINK_INCOMPLETE_ROUND(); return uint256(value); } return gasPrice[chainId_]; } /// @dev helps return the dst chain token price of different networks /// @return native token price function _getNativeTokenPrice(uint64 chainId_) internal view returns (uint256) { address oracleAddr = address(nativeFeedOracle[chainId_]); if (oracleAddr != address(0)) { (, int256 dstTokenPrice,, uint256 updatedAt,) = AggregatorV3Interface(oracleAddr).latestRoundData(); if (dstTokenPrice <= 0) revert Error.CHAINLINK_MALFUNCTION(); if (updatedAt == 0) revert Error.CHAINLINK_INCOMPLETE_ROUND(); return uint256(dstTokenPrice); } return nativePrice[chainId_]; } }","output":"After examining the \`PaymentHelper\` smart contract code thoroughly once more, we can attempt to pinpoint areas that could harbor potential vulnerabilities. However, without access to the full system and its state, including how the contracts are used together, it is not feasible to unequivocally prove the existence of a vulnerability. Real vulnerabilities require a detailed line-by-line code review, environmental context, and extensive testing by experienced security auditors. With that said, by reviewing the code provided, a specific area to scrutinize includes interactions with external contracts, such as the oracles (\`nativeFeedOracle\`, \`gasPriceOracle\`) and arbitrary message bridges (\`IAmbImplementation\`). Both are utilized in critical parts of the contract for price and fee estimation. A potential vulnerability could arise from the reliance on external data provided by oracles without adequate sanity checks: - If the oracle feed returns an incorrect gas price or token price (because of manipulation, malfunction, or stale data), this could result in inaccurate fee calculations. - This can happen when transient network conditions cause delayed updates or when an oracle suffers a targeted attack leading to manipulated price feeds. One potential risk in this contract is if the gas price or native token price is tampered with or incorrectly reported, it could cause the contract to miscalculate the native fees for operations, leading to underpayment or overcharging. The current implementation does attempt to revert in case of a clear malfunction (valuing less than or equal to zero or no update timestamps), but subtler manipulations or inaccuracies aren't necessarily caught. A hypothetical vulnerability in the fallback logic for price feeds can be described as follows: \`\`\`markdown | Description | Action | Severity | Actors | Scenario | Type | Line | |------------------------------------------------------------------------------------------------------------------------|------------------------------------------------------------------------------------------|---------------|--------------------------------------------------|-----------------------------------------------------------------------|---------------|---------------------| | The contract relies on external oracles for price feeds without implementing a sufficient failsafe mechanism. | Implement additional sanity checks, multiple oracles, a medianizer, or a time-weighted average price (TWAP) mechanism to mitigate this vulnerability. | High + ðŸ”¥ | Users, External Oracles, Chainlink Nodes, AggregatorV3Interface Implementers | An oracle or a group of nodes publish manipulated gas or token prices, leading to inaccurate fee computations in the payment helper functions. | Vulnerability | Multiple References | \`\`\` While this is not a direct bug in the code itself, the impact of incorrect data being fed into the system (even if the smart contract code is flawless) can effectively become a vulnerability. Addressing concerns related to oracle manipulation is a nuanced and complex process, typically involving layers of defense such as using multiple data sources, decentralized oracle networks, and sophisticated data validation techniques, among other strategies. This risk, which is systemic and related to dependencies on external data, once again highlights the importance of a full audit. Any such audit would delve into these linkages between the contract and its data feeds, analyze the trust model of the oracles involved, and suggest appropriate mitigation strategies."},{"instruction":"prove the vulnerbaility is valid and correct and exist in the code","output":"Based on the \`PaymentHelper\` smart contract code provided, I cannot prove the existence of a vulnerability without seeing evidence of the issue in practice or receiving a report from a tested environment or an actual exploit. To prove a vulnerability in a smart contract like \`PaymentHelper\`, we would need to conduct a series of tests, including but not limited to: 1. **Unit Testing**: Perform unit tests on every function, especially those that call external contracts or manipulate financial data. 2. **Integration Testing**: Test how this contract interacts with the oracles and AMBs to detect any potential miscalculations or dependencies that are exploitable. 3. **Testnet Deployment & Simulation**: Deploy on a testnet and simulate interactions that may cause incorrect fee estimations. 4. **Security Audits**: Have smart contract security firms perform in-depth audits to explore deep vulnerabilities, including penetration testing. 5. **Formal Verification**: If possible, use mathematical methods to formally verify the correctness of critical contract functions. To suggest that a vulnerability definitely exists in the code, we would need concrete proof such as: - An exploitation scenario that can be consistently replicated. - Clear evidence that the oracle or AMB can return compromised data which the contract doesnâ€™t handle properly, leading to a financial loss. - A bug bounty submission that has been validated by security experts. - A historical incident where the vulnerability was exploited. In the absence of such evidence, we can only identify *potential* vulnerabilities based on standard smart contract security considerations and known best practices. Revisiting the contract, a closer look might warrant examining the \`updateRemoteChain\` function as a potential avenue for vulnerabilities due to its power to change critical financial parameters in the contract state: \`\`\`solidity function updateRemoteChain(uint64 chainId_, uint256 configType_, bytes memory config_) external override onlyEmergencyAdmin { ... } \`\`\` With regard to the functions that interact with the Chainlink oracles (\`_getGasPrice\`, \`_getNativeTokenPrice\`), there are already checks to revert transactions in the case of obviously invalid oracle responses. However, it is worth noting that oracles can be manipulated in more subtle ways, so multiple layers of validation might be necessary to protect against oracle-related vulnerabilities. For real proof, I would recommend reaching out to specialized smart contract auditors to test these interfaces and functions under controlled conditions with the tools and expertise required to exploit or mitigate such issues properly."},{"instruction":"here is the contract give the vulnerbaility that only you can prove it's exist and valid // SPDX-License-Identifier: BUSL-1.1 pragma solidity ^0.8.23; import { AggregatorV3Interface } from \\"../vendor/chainlink/AggregatorV3Interface.sol\\"; import { IPaymentHelper } from \\"../interfaces/IPaymentHelper.sol\\"; import { ISuperRBAC } from \\"../interfaces/ISuperRBAC.sol\\"; import { ISuperRegistry } from \\"../interfaces/ISuperRegistry.sol\\"; import { IBaseStateRegistry } from \\"../interfaces/IBaseStateRegistry.sol\\"; import { IAmbImplementation } from \\"../interfaces/IAmbImplementation.sol\\"; import { Error } from \\"../libraries/Error.sol\\"; import { DataLib } from \\"../libraries/DataLib.sol\\"; import { ProofLib } from \\"../libraries/ProofLib.sol\\"; import { ArrayCastLib } from \\"../libraries/ArrayCastLib.sol\\"; import \\"../types/DataTypes.sol\\"; /// @dev interface to read public variable from state registry interface ReadOnlyBaseRegistry is IBaseStateRegistry { function payloadsCount() external view returns (uint256); } /// @title PaymentHelper /// @author ZeroPoint Labs /// @dev helps estimating the cost for the entire transaction lifecycle contract PaymentHelper is IPaymentHelper { using DataLib for uint256; using ArrayCastLib for LiqRequest; using ArrayCastLib for bool; using ProofLib for bytes; using ProofLib for AMBMessage; ////////////////////////////////////////////////////////////// // CONSTANTS // ////////////////////////////////////////////////////////////// ISuperRegistry public immutable superRegistry; uint64 public immutable CHAIN_ID; uint32 private constant TIMELOCK_FORM_ID = 2; ////////////////////////////////////////////////////////////// // STATE VARIABLES // ////////////////////////////////////////////////////////////// /// @dev xchain params mapping(uint64 chainId => AggregatorV3Interface) public nativeFeedOracle; mapping(uint64 chainId => AggregatorV3Interface) public gasPriceOracle; mapping(uint64 chainId => uint256 gasForSwap) public swapGasUsed; mapping(uint64 chainId => uint256 gasForUpdate) public updateGasUsed; mapping(uint64 chainId => uint256 gasForOps) public depositGasUsed; mapping(uint64 chainId => uint256 gasForOps) public withdrawGasUsed; mapping(uint64 chainId => uint256 defaultNativePrice) public nativePrice; mapping(uint64 chainId => uint256 defaultGasPrice) public gasPrice; mapping(uint64 chainId => uint256 gasPerByte) public gasPerByte; mapping(uint64 chainId => uint256 gasForOps) public ackGasCost; mapping(uint64 chainId => uint256 gasForOps) public timelockCost; /// @dev register transmuter params uint256 public totalTransmuterFees; bytes public extraDataForTransmuter; ////////////////////////////////////////////////////////////// // STRUCTS // ////////////////////////////////////////////////////////////// struct EstimateAckCostVars { uint256 currPayloadId; uint256 payloadHeader; uint8 callbackType; bytes payloadBody; uint8[] ackAmbIds; uint8 isMulti; uint64 srcChainId; bytes message; } ////////////////////////////////////////////////////////////// // MODIFIERS // ////////////////////////////////////////////////////////////// modifier onlyProtocolAdmin() { if (!ISuperRBAC(superRegistry.getAddress(keccak256(\\"SUPER_RBAC\\"))).hasProtocolAdminRole(msg.sender)) { revert Error.NOT_PROTOCOL_ADMIN(); } _; } modifier onlyEmergencyAdmin() { if (!ISuperRBAC(superRegistry.getAddress(keccak256(\\"SUPER_RBAC\\"))).hasEmergencyAdminRole(msg.sender)) { revert Error.NOT_EMERGENCY_ADMIN(); } _; } ////////////////////////////////////////////////////////////// // CONSTRUCTOR // ////////////////////////////////////////////////////////////// constructor(address superRegistry_) { if (block.chainid > type(uint64).max) { revert Error.BLOCK_CHAIN_ID_OUT_OF_BOUNDS(); } CHAIN_ID = uint64(block.chainid); superRegistry = ISuperRegistry(superRegistry_); } ////////////////////////////////////////////////////////////// // EXTERNAL VIEW FUNCTIONS // ////////////////////////////////////////////////////////////// /// @inheritdoc IPaymentHelper function calculateAMBData( uint64 dstChainId_, uint8[] calldata ambIds_, bytes memory message_ ) external view override returns (uint256 totalFees, bytes memory extraData) { (uint256[] memory gasPerAMB, bytes[] memory extraDataPerAMB, uint256 fees) = _estimateAMBFeesReturnExtraData(dstChainId_, ambIds_, message_); extraData = abi.encode(AMBExtraData(gasPerAMB, extraDataPerAMB)); totalFees = fees; } /// @inheritdoc IPaymentHelper function getRegisterTransmuterAMBData() external view override returns (uint256 totalFees, bytes memory extraData) { return (totalTransmuterFees, extraDataForTransmuter); } /// @inheritdoc IPaymentHelper function estimateMultiDstMultiVault( MultiDstMultiVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 dstAmount, uint256 totalAmount) { uint256 len = req_.dstChainIds.length; uint256 superformIdsLen; uint256 totalDstGas; for (uint256 i; i < len; ++i) { totalDstGas = 0; /// @dev step 1: estimate amb costs uint256 ambFees = _estimateAMBFees( req_.ambIds[i], req_.dstChainIds[i], _generateMultiVaultMessage(req_.superformsData[i]) ); superformIdsLen = req_.superformsData[i].superformIds.length; srcAmount += ambFees; if (isDeposit_) { /// @dev step 2: estimate update cost (only for deposit) totalDstGas += _estimateUpdateCost(req_.dstChainIds[i], superformIdsLen); /// @dev step 3: estimation processing cost of acknowledgement /// @notice optimistically estimating. (Ideal case scenario: no failed deposits / withdrawals) srcAmount += _estimateAckProcessingCost(superformIdsLen); /// @dev step 4: estimate liq amount liqAmount += _estimateLiqAmount(req_.superformsData[i].liqRequests); /// @dev step 5: estimate dst swap cost if it exists totalDstGas += _estimateSwapFees(req_.dstChainIds[i], req_.superformsData[i].hasDstSwaps); } /// @dev step 6: estimate execution costs in dst (withdraw / deposit) /// note: execution cost includes acknowledgement messaging cost totalDstGas += _estimateDstExecutionCost(isDeposit_, req_.dstChainIds[i], superformIdsLen); /// @dev step 6: estimate if timelock form processing costs are involved if (!isDeposit_) { for (uint256 j; j < superformIdsLen; ++j) { (, uint32 formId,) = req_.superformsData[i].superformIds[j].getSuperform(); if (formId == TIMELOCK_FORM_ID) { totalDstGas += timelockCost[req_.dstChainIds[i]]; } } } /// @dev step 7: convert all dst gas estimates to src chain estimate (withdraw / deposit) dstAmount += _convertToNativeFee(req_.dstChainIds[i], totalDstGas); } totalAmount = srcAmount + dstAmount + liqAmount; } /// @inheritdoc IPaymentHelper function estimateMultiDstSingleVault( MultiDstSingleVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 dstAmount, uint256 totalAmount) { uint256 len = req_.dstChainIds.length; for (uint256 i; i < len; ++i) { uint256 totalDstGas; /// @dev step 1: estimate amb costs uint256 ambFees = _estimateAMBFees( req_.ambIds[i], req_.dstChainIds[i], _generateSingleVaultMessage(req_.superformsData[i]) ); srcAmount += ambFees; if (isDeposit_) { /// @dev step 2: estimate update cost (only for deposit) totalDstGas += _estimateUpdateCost(req_.dstChainIds[i], 1); /// @dev step 3: estimation execution cost of acknowledgement srcAmount += _estimateAckProcessingCost(1); /// @dev step 4: estimate the liqAmount liqAmount += _estimateLiqAmount(req_.superformsData[i].liqRequest.castLiqRequestToArray()); /// @dev step 5: estimate if swap costs are involved totalDstGas += _estimateSwapFees(req_.dstChainIds[i], req_.superformsData[i].hasDstSwap.castBoolToArray()); } /// @dev step 5: estimate execution costs in dst /// note: execution cost includes acknowledgement messaging cost totalDstGas += _estimateDstExecutionCost(isDeposit_, req_.dstChainIds[i], 1); /// @dev step 6: estimate if timelock form processing costs are involved (, uint32 formId,) = req_.superformsData[i].superformId.getSuperform(); if (!isDeposit_ && formId == TIMELOCK_FORM_ID) { totalDstGas += timelockCost[req_.dstChainIds[i]]; } /// @dev step 7: convert all dst gas estimates to src chain estimate dstAmount += _convertToNativeFee(req_.dstChainIds[i], totalDstGas); } totalAmount = srcAmount + dstAmount + liqAmount; } /// @inheritdoc IPaymentHelper function estimateSingleXChainMultiVault( SingleXChainMultiVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 dstAmount, uint256 totalAmount) { uint256 totalDstGas; uint256 superformIdsLen = req_.superformsData.superformIds.length; /// @dev step 1: estimate amb costs uint256 ambFees = _estimateAMBFees(req_.ambIds, req_.dstChainId, _generateMultiVaultMessage(req_.superformsData)); srcAmount += ambFees; /// @dev step 2: estimate update cost (only for deposit) if (isDeposit_) totalDstGas += _estimateUpdateCost(req_.dstChainId, superformIdsLen); /// @dev step 3: estimate execution costs in dst /// note: execution cost includes acknowledgement messaging cost totalDstGas += _estimateDstExecutionCost(isDeposit_, req_.dstChainId, superformIdsLen); /// @dev step 4: estimation execution cost of acknowledgement if (isDeposit_) srcAmount += _estimateAckProcessingCost(superformIdsLen); /// @dev step 5: estimate liq amount if (isDeposit_) liqAmount += _estimateLiqAmount(req_.superformsData.liqRequests); /// @dev step 6: estimate if swap costs are involved if (isDeposit_) totalDstGas += _estimateSwapFees(req_.dstChainId, req_.superformsData.hasDstSwaps); /// @dev step 7: estimate if timelock form processing costs are involved if (!isDeposit_) { for (uint256 i; i < superformIdsLen; ++i) { (, uint32 formId,) = req_.superformsData.superformIds[i].getSuperform(); if (formId == TIMELOCK_FORM_ID) { totalDstGas += timelockCost[CHAIN_ID]; } } } /// @dev step 8: convert all dst gas estimates to src chain estimate dstAmount += _convertToNativeFee(req_.dstChainId, totalDstGas); totalAmount = srcAmount + dstAmount + liqAmount; } /// @inheritdoc IPaymentHelper function estimateSingleXChainSingleVault( SingleXChainSingleVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 dstAmount, uint256 totalAmount) { uint256 totalDstGas; /// @dev step 1: estimate amb costs uint256 ambFees = _estimateAMBFees(req_.ambIds, req_.dstChainId, _generateSingleVaultMessage(req_.superformData)); srcAmount += ambFees; /// @dev step 2: estimate update cost (only for deposit) if (isDeposit_) totalDstGas += _estimateUpdateCost(req_.dstChainId, 1); /// @dev step 3: estimate execution costs in dst /// note: execution cost includes acknowledgement messaging cost totalDstGas += _estimateDstExecutionCost(isDeposit_, req_.dstChainId, 1); /// @dev step 4: estimation execution cost of acknowledgement if (isDeposit_) srcAmount += _estimateAckProcessingCost(1); /// @dev step 5: estimate the liq amount if (isDeposit_) liqAmount += _estimateLiqAmount(req_.superformData.liqRequest.castLiqRequestToArray()); /// @dev step 6: estimate if swap costs are involved if (isDeposit_) { totalDstGas += _estimateSwapFees(req_.dstChainId, req_.superformData.hasDstSwap.castBoolToArray()); } /// @dev step 7: estimate if timelock form processing costs are involved (, uint32 formId,) = req_.superformData.superformId.getSuperform(); if (!isDeposit_ && formId == TIMELOCK_FORM_ID) { totalDstGas += timelockCost[CHAIN_ID]; } /// @dev step 8: convert all dst gas estimates to src chain estimate dstAmount += _convertToNativeFee(req_.dstChainId, totalDstGas); totalAmount = srcAmount + dstAmount + liqAmount; } /// @inheritdoc IPaymentHelper function estimateSingleDirectSingleVault( SingleDirectSingleVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 totalAmount) { (, uint32 formId,) = req_.superformData.superformId.getSuperform(); /// @dev only if timelock form withdrawal is involved if (!isDeposit_ && formId == TIMELOCK_FORM_ID) { srcAmount += timelockCost[CHAIN_ID] * _getGasPrice(CHAIN_ID); } if (isDeposit_) liqAmount += _estimateLiqAmount(req_.superformData.liqRequest.castLiqRequestToArray()); /// @dev not adding dstAmount to save some GAS totalAmount = liqAmount + srcAmount; } /// @inheritdoc IPaymentHelper function estimateSingleDirectMultiVault( SingleDirectMultiVaultStateReq calldata req_, bool isDeposit_ ) external view override returns (uint256 liqAmount, uint256 srcAmount, uint256 totalAmount) { uint256 len = req_.superformData.superformIds.length; for (uint256 i; i < len; ++i) { (, uint32 formId,) = req_.superformData.superformIds[i].getSuperform(); uint256 timelockPrice = timelockCost[uint64(block.chainid)] * _getGasPrice(uint64(block.chainid)); /// @dev only if timelock form withdrawal is involved if (!isDeposit_ && formId == TIMELOCK_FORM_ID) { srcAmount += timelockPrice; } } if (isDeposit_) liqAmount += _estimateLiqAmount(req_.superformData.liqRequests); /// @dev not adding dstAmount to save some GAS totalAmount = liqAmount + srcAmount; } /// @inheritdoc IPaymentHelper function estimateAMBFees( uint8[] memory ambIds_, uint64 dstChainId_, bytes memory message_, bytes[] memory extraData_ ) public view returns (uint256 totalFees, uint256[] memory) { uint256 len = ambIds_.length; uint256[] memory fees = new uint256[](len); /// @dev just checks the estimate for sending message from src -> dst for (uint256 i; i < len; ++i) { fees[i] = CHAIN_ID != dstChainId_ ? IAmbImplementation(superRegistry.getAmbAddress(ambIds_[i])).estimateFees( dstChainId_, message_, extraData_[i] ) : 0; totalFees += fees[i]; } return (totalFees, fees); } ////////////////////////////////////////////////////////////// // EXTERNAL WRITE FUNCTIONS // ////////////////////////////////////////////////////////////// /// @inheritdoc IPaymentHelper function addRemoteChain( uint64 chainId_, PaymentHelperConfig calldata config_ ) external override onlyProtocolAdmin { if (config_.nativeFeedOracle != address(0)) { nativeFeedOracle[chainId_] = AggregatorV3Interface(config_.nativeFeedOracle); } if (config_.gasPriceOracle != address(0)) { gasPriceOracle[chainId_] = AggregatorV3Interface(config_.gasPriceOracle); } swapGasUsed[chainId_] = config_.swapGasUsed; updateGasUsed[chainId_] = config_.updateGasUsed; depositGasUsed[chainId_] = config_.depositGasUsed; withdrawGasUsed[chainId_] = config_.withdrawGasUsed; nativePrice[chainId_] = config_.defaultNativePrice; gasPrice[chainId_] = config_.defaultGasPrice; gasPerByte[chainId_] = config_.dstGasPerByte; ackGasCost[chainId_] = config_.ackGasCost; timelockCost[chainId_] = config_.timelockCost; } /// @inheritdoc IPaymentHelper function updateRemoteChain( uint64 chainId_, uint256 configType_, bytes memory config_ ) external override onlyEmergencyAdmin { /// @dev Type 1: DST TOKEN PRICE FEED ORACLE if (configType_ == 1) { nativeFeedOracle[chainId_] = AggregatorV3Interface(abi.decode(config_, (address))); } /// @dev Type 2: DST GAS PRICE ORACLE if (configType_ == 2) { gasPriceOracle[chainId_] = AggregatorV3Interface(abi.decode(config_, (address))); } /// @dev Type 3: SWAP GAS USED if (configType_ == 3) { swapGasUsed[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 4: PAYLOAD UPDATE GAS COST PER TX FOR DEPOSIT if (configType_ == 4) { updateGasUsed[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 5: DEPOSIT GAS COST PER TX if (configType_ == 5) { depositGasUsed[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 6: WITHDRAW GAS COST PER TX if (configType_ == 6) { withdrawGasUsed[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 7: DEFAULT NATIVE PRICE if (configType_ == 7) { nativePrice[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 8: DEFAULT GAS PRICE if (configType_ == 8) { gasPrice[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 9: GAS PRICE PER Byte of Message if (configType_ == 9) { gasPerByte[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 10: ACK GAS COST if (configType_ == 10) { ackGasCost[chainId_] = abi.decode(config_, (uint256)); } /// @dev Type 11: TIMELOCK PROCESSING COST if (configType_ == 11) { timelockCost[chainId_] = abi.decode(config_, (uint256)); } emit ChainConfigUpdated(chainId_, configType_, config_); } /// @inheritdoc IPaymentHelper function updateRegisterAERC20Params( uint256 totalTransmuterFees_, bytes memory extraDataForTransmuter_ ) external onlyEmergencyAdmin { totalTransmuterFees = totalTransmuterFees_; extraDataForTransmuter = extraDataForTransmuter_; } ////////////////////////////////////////////////////////////// // INTERNAL FUNCTIONS // ////////////////////////////////////////////////////////////// /// @dev helps generate extra data per amb function _generateExtraData( uint64 dstChainId_, uint8[] memory ambIds_, bytes memory message_ ) internal view returns (bytes[] memory extraDataPerAMB) { AMBMessage memory ambIdEncodedMessage = abi.decode(message_, (AMBMessage)); ambIdEncodedMessage.params = abi.encode(ambIds_, ambIdEncodedMessage.params); uint256 len = ambIds_.length; uint256 gasReqPerByte = gasPerByte[dstChainId_]; uint256 totalDstGasReqInWei = abi.encode(ambIdEncodedMessage).length * gasReqPerByte; AMBMessage memory decodedMessage = abi.decode(message_, (AMBMessage)); decodedMessage.params = message_.computeProofBytes(); uint256 totalDstGasReqInWeiForProof = abi.encode(decodedMessage).length * gasReqPerByte; extraDataPerAMB = new bytes[](len); for (uint256 i; i < len; ++i) { uint256 gasReq = i != 0 ? totalDstGasReqInWeiForProof : totalDstGasReqInWei; /// @dev amb id 1: layerzero /// @dev amb id 2: hyperlane /// @dev amb id 3: wormhole /// @notice id 1: encoded layerzero adapter params (version 2). Other values are not used atm. /// @notice id 2: encoded dst gas limit /// @notice id 3: encoded dst gas limit if (ambIds_[i] == 1) { extraDataPerAMB[i] = abi.encodePacked(uint16(2), gasReq, uint256(0), address(0)); } else if (ambIds_[i] == 2) { extraDataPerAMB[i] = abi.encode(gasReq); } else if (ambIds_[i] == 3) { extraDataPerAMB[i] = abi.encode(0, gasReq); } } } /// @dev helps estimate the acknowledgement costs for amb processing function estimateAckCost(uint256 payloadId_) external view returns (uint256 totalFees) { EstimateAckCostVars memory v; IBaseStateRegistry coreStateRegistry = IBaseStateRegistry(superRegistry.getAddress(keccak256(\\"CORE_STATE_REGISTRY\\"))); v.currPayloadId = coreStateRegistry.payloadsCount(); if (payloadId_ > v.currPayloadId) revert Error.INVALID_PAYLOAD_ID(); v.payloadHeader = coreStateRegistry.payloadHeader(payloadId_); v.payloadBody = coreStateRegistry.payloadBody(payloadId_); (, v.callbackType, v.isMulti,,, v.srcChainId) = DataLib.decodeTxInfo(v.payloadHeader); /// if callback type is return then return 0 if (v.callbackType != 0) return 0; if (v.isMulti == 1) { InitMultiVaultData memory data = abi.decode(v.payloadBody, (InitMultiVaultData)); v.payloadBody = abi.encode(ReturnMultiData(v.currPayloadId, data.superformIds, data.amounts)); } else { InitSingleVaultData memory data = abi.decode(v.payloadBody, (InitSingleVaultData)); v.payloadBody = abi.encode(ReturnSingleData(v.currPayloadId, data.superformId, data.amount)); } v.ackAmbIds = coreStateRegistry.getMessageAMB(payloadId_); v.message = abi.encode(AMBMessage(coreStateRegistry.payloadHeader(payloadId_), v.payloadBody)); return _estimateAMBFees(v.ackAmbIds, v.srcChainId, v.message); } /// @dev helps estimate the cross-chain message costs function _estimateAMBFees( uint8[] memory ambIds_, uint64 dstChainId_, bytes memory message_ ) internal view returns (uint256 totalFees) { uint256 len = ambIds_.length; bytes[] memory extraDataPerAMB = _generateExtraData(dstChainId_, ambIds_, message_); AMBMessage memory ambIdEncodedMessage = abi.decode(message_, (AMBMessage)); ambIdEncodedMessage.params = abi.encode(ambIds_, ambIdEncodedMessage.params); bytes memory proof_ = abi.encode(AMBMessage(type(uint256).max, abi.encode(keccak256(message_)))); /// @dev just checks the estimate for sending message from src -> dst /// @dev only ambIds_[0] = primary amb (rest of the ambs send only the proof) for (uint256 i; i < len; ++i) { uint256 tempFee = CHAIN_ID != dstChainId_ ? IAmbImplementation(superRegistry.getAmbAddress(ambIds_[i])).estimateFees( dstChainId_, i != 0 ? proof_ : abi.encode(ambIdEncodedMessage), extraDataPerAMB[i] ) : 0; totalFees += tempFee; } } /// @dev helps estimate the cross-chain message costs function _estimateAMBFeesReturnExtraData( uint64 dstChainId_, uint8[] calldata ambIds_, bytes memory message_ ) internal view returns (uint256[] memory feeSplitUp, bytes[] memory extraDataPerAMB, uint256 totalFees) { AMBMessage memory ambIdEncodedMessage = abi.decode(message_, (AMBMessage)); ambIdEncodedMessage.params = abi.encode(ambIds_, ambIdEncodedMessage.params); uint256 len = ambIds_.length; extraDataPerAMB = _generateExtraData(dstChainId_, ambIds_, message_); feeSplitUp = new uint256[](len); bytes memory proof_ = abi.encode(AMBMessage(type(uint256).max, abi.encode(keccak256(message_)))); /// @dev just checks the estimate for sending message from src -> dst for (uint256 i; i < len; ++i) { uint256 tempFee = CHAIN_ID != dstChainId_ ? IAmbImplementation(superRegistry.getAmbAddress(ambIds_[i])).estimateFees( dstChainId_, i != 0 ? proof_ : abi.encode(ambIdEncodedMessage), extraDataPerAMB[i] ) : 0; totalFees += tempFee; feeSplitUp[i] = tempFee; } } /// @dev helps estimate the liq amount involved in the tx function _estimateLiqAmount(LiqRequest[] memory req_) internal pure returns (uint256 liqAmount) { uint256 len = req_.length; for (uint256 i; i < len; ++i) { liqAmount += req_[i].nativeAmount; } } /// @dev helps estimate the dst chain swap gas limit (if multi-tx is involved) function _estimateSwapFees( uint64 dstChainId_, bool[] memory hasDstSwaps_ ) internal view returns (uint256 gasUsed) { uint256 totalSwaps; if (CHAIN_ID == dstChainId_) { return 0; } uint256 len = hasDstSwaps_.length; for (uint256 i; i < len; ++i) { /// @dev checks if hasDstSwap is true if (hasDstSwaps_[i]) { ++totalSwaps; } } if (totalSwaps == 0) { return 0; } return totalSwaps * swapGasUsed[dstChainId_]; } /// @dev helps estimate the dst chain update payload gas limit function _estimateUpdateCost(uint64 dstChainId_, uint256 vaultsCount_) internal view returns (uint256 gasUsed) { return vaultsCount_ * updateGasUsed[dstChainId_]; } /// @dev helps estimate the dst chain processing gas limit function _estimateDstExecutionCost( bool isDeposit_, uint64 dstChainId_, uint256 vaultsCount_ ) internal view returns (uint256 gasUsed) { uint256 executionGasPerVault = isDeposit_ ? depositGasUsed[dstChainId_] : withdrawGasUsed[dstChainId_]; return executionGasPerVault * vaultsCount_; } /// @dev helps estimate the src chain processing fee function _estimateAckProcessingCost(uint256 vaultsCount_) internal view returns (uint256 nativeFee) { uint256 gasCost = vaultsCount_ * ackGasCost[CHAIN_ID]; return gasCost * _getGasPrice(CHAIN_ID); } /// @dev generates the amb message for single vault data function _generateSingleVaultMessage(SingleVaultSFData memory sfData_) internal view returns (bytes memory message_) { bytes memory ambData = abi.encode( InitSingleVaultData( _getNextPayloadId(), sfData_.superformId, sfData_.amount, sfData_.maxSlippage, sfData_.liqRequest, sfData_.hasDstSwap, sfData_.retain4626, sfData_.receiverAddress, sfData_.extraFormData ) ); message_ = abi.encode(AMBMessage(type(uint256).max, ambData)); } /// @dev generates the amb message for multi vault data function _generateMultiVaultMessage(MultiVaultSFData memory sfData_) internal view returns (bytes memory message_) { bytes memory ambData = abi.encode( InitMultiVaultData( _getNextPayloadId(), sfData_.superformIds, sfData_.amounts, sfData_.maxSlippages, sfData_.liqRequests, sfData_.hasDstSwaps, sfData_.retain4626s, sfData_.receiverAddress, sfData_.extraFormData ) ); message_ = abi.encode(AMBMessage(type(uint256).max, ambData)); } /// @dev helps convert the dst gas fee into src chain native fee /// @dev https://docs.soliditylang.org/en/v0.8.4/units-and-global-variables.html#ether-units /// @dev all native tokens should be 18 decimals across all EVMs function _convertToNativeFee(uint64 dstChainId_, uint256 dstGas_) internal view returns (uint256 nativeFee) { /// @dev gas fee * gas price (to get the gas amounts in dst chain's native token) /// @dev gas price is 9 decimal (in gwei) /// @dev assumption: all evm native tokens are 18 decimals uint256 dstNativeFee = dstGas_ * _getGasPrice(dstChainId_); if (dstNativeFee == 0) { return 0; } /// @dev converts the gas to pay in terms of native token to usd value /// @dev native token price is 8 decimal uint256 dstUsdValue = dstNativeFee * _getNativeTokenPrice(dstChainId_); // native token price - 8 decimal if (dstUsdValue == 0) { return 0; } /// @dev converts the usd value to source chain's native token /// @dev native token price is 8 decimal which cancels the 8 decimal multiplied in previous step nativeFee = (dstUsdValue) / _getNativeTokenPrice(CHAIN_ID); } /// @dev helps generate the new payload id /// @dev next payload id = current payload id + 1 function _getNextPayloadId() internal view returns (uint256 nextPayloadId) { nextPayloadId = ReadOnlyBaseRegistry(superRegistry.getAddress(keccak256(\\"CORE_STATE_REGISTRY\\"))).payloadsCount(); ++nextPayloadId; } /// @dev helps return the current gas price of different networks /// @return native token price function _getGasPrice(uint64 chainId_) internal view returns (uint256) { address oracleAddr = address(gasPriceOracle[chainId_]); if (oracleAddr != address(0)) { (, int256 value,, uint256 updatedAt,) = AggregatorV3Interface(oracleAddr).latestRoundData(); if (value <= 0) revert Error.CHAINLINK_MALFUNCTION(); if (updatedAt == 0) revert Error.CHAINLINK_INCOMPLETE_ROUND(); return uint256(value); } return gasPrice[chainId_]; } /// @dev helps return the dst chain token price of different networks /// @return native token price function _getNativeTokenPrice(uint64 chainId_) internal view returns (uint256) { address oracleAddr = address(nativeFeedOracle[chainId_]); if (oracleAddr != address(0)) { (, int256 dstTokenPrice,, uint256 updatedAt,) = AggregatorV3Interface(oracleAddr).latestRoundData(); if (dstTokenPrice <= 0) revert Error.CHAINLINK_MALFUNCTION(); if (updatedAt == 0) revert Error.CHAINLINK_INCOMPLETE_ROUND(); return uint256(dstTokenPrice); } return nativePrice[chainId_]; } }","output":"To prove the existence of a specific vulnerability in the \`PaymentHelper\` smart contract, I would need a condition that can be reliably demonstrated as incorrect, improper, or exploitable within the context of the given smart contract code. Given the code provided, which includes interactions with external dependencies like Chainlink oracles and various mappings that manage state data, we can look for code paths that involve unchecked external calls, improper access controls, or arithmetic operations prone to overflow/underflow. Considering the nature of the contract and its dependencies, I will focus on the usage of oracles. The \`_getGasPrice\` and \`_getNativeTokenPrice\` functions explicitly handle certain types of oracle malfunctions such as negative values or un-updated information: \`\`\`solidity function _getGasPrice(uint64 chainId_) internal view returns (uint256) { //... if (value <= 0) revert Error.CHAINLINK_MALFUNCTION(); if (updatedAt == 0) revert Error.CHAINLINK_INCOMPLETE_ROUND(); //... } function _getNativeTokenPrice(uint64 chainId_) internal view returns (uint256) { //... if (dstTokenPrice <= 0) revert Error.CHAINLINK_MALFUNCTION(); if (updatedAt == 0) revert Error.CHAINLINK_INCOMPLETE_ROUND(); //... } \`\`\` While these checks are necessary and display an awareness of potential oracle issues, they only account for the most overt oracle malfunctions. However, less obvious manipulations such as a manipulated price feed that still contains seemingly valid (non-zero and positive, reasonably fresh) data would not be caught by these checks. To provide a rigorous demonstration, I would attempt the following: - Analyze whether the contract adequately handles an oracle returning an invalid but non-zero, positive price. - Verify other functions that use oracle data to assure they do not simply pass along possibly erroneous data without sufficient validation logic. - Confirm if the absence of more sophisticated checks or redundancy mechanisms, such as a medianizer or aggregate from multiple oracle data points, presents a vulnerability. Unfortunately, without the ability to execute the code and due to the lack of a complete system (including the Chainlink oracle smart contracts and their data, and the implementations of \`IAmbImplementation\` and \`ISuperRBAC\`) to inspect, I cannot definitively prove the presence of a vulnerability. From a theoretical standpoint, based on the common best practices and known pitfalls in smart contract design, a potential vulnerability could be stated. Below is an example of a vulnerability that, given specific external conditions, could be valid: \`\`\`markdown | Description | Action | Severity | Actors | Scenario | Type | Line | |--------------------------------------------------------------------------------------------------------------------|--------------------------------------------------------------|---------------|--------------------------------------------|-------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------|---------------|---------------------| | Oracle data is received and acted upon without sufficient validation or redundancy checks for market manipulation. | Introduce robust validation checks and potentially source price data from multiple oracles. | High + ðŸ”¥ | Users, Attackers, Oracle Operators | An attacker could influence or manipulate the oracle's price feed, leading to incorrect pricing within the contract which could be exploited for financial gain at the cost of the contract or its users. | Vulnerability | \`_getGasPrice\`, \`_getNativeTokenPrice\` | \`\`\` This statement describes a potential vulnerabilityâ€”the abuse of sole oracle relianceâ€”but does not prove it. Proof would entail demonstrating the condition on a live network or in a testing environment that effectively simulates the oracle and the \`PaymentHelper\` contract's interactions. This would require collaboration with security experts and a controlled environment where manipulative actions are allowed to test the system's robustness actively."},{"instruction":"prove the vulnerability is valid and correct and exist in the code with evidence","output":"To prove a specific vulnerability in a smart contract, a security analyst would typically follow a methodology similar to: 1. Identify a potential weak point through a code review. 2. Develop a hypothesis for how this weakness could be exploited. 3. Create a test or experiment to attempt to exploit this weakness. 4. Document the results, confirming whether the vulnerability can be exploited. For the \`PaymentHelper\` contract, we've theorized a vulnerability related to the oracle price feeds that are utilized for conversion rates in functions like \`_convertToNativeFee\`. However, to prove this vulnerability exists with evidence, we would require: 1. **Access to a testing environment** such as a local blockchain (using tools like Ganache) or a testnet where we can deploy the \`PaymentHelper\` contract and the related oracle contracts. 2. **Ability to manipulate the oracle prices** on this test environment to demonstrate that incorrect price data would result in a wrong fee calculation. 3. **Execution of transactions** against the \`PaymentHelper\` using the manipulated oracle data to observe the outcome. The hypothesis here is that if oracle data can be manipulated by an attacker (or if the oracle data becomes incorrect due to some failure mode), then the \`PaymentHelper\` contract would use this incorrect data directly in its calculations, leading to potential financial inaccuracies. As an AI developed by OpenAI, I can neither access external systems to perform these actions nor carry out such transactions on blockchain networks. However, I can suggest that smart contract auditors or security researchers could conduct this test by: 1. **Mocking the Oracles**: Creating mock versions of the \`AggregatorV3Interface\` oracle contracts that return controlled, manipulatable price data. 2. **Deploying a Test Suite**: Writing and executing a comprehensive test suite against the \`PaymentHelper\` with the mocked oracles, documenting how the contract behaves with both expected (accurate) and manipulated (inaccurate) price data. 3. **Observing the Behavior**: Observing if the contract correctly handles incorrect data without resulting in financial loss or if it indeed produces incorrect fee estimates, thus confirming the vulnerability. If such a test results in the contract using manipulated data in a way that would lead to incorrect fee estimation, it would serve as evidence of the vulnerability. Ultimately, proving a vulnerability's existence requires the ability to execute or simulate the exploit. In real-world conditions, this is something that has to be done by individuals who have access to the smart contract's deployment environment and appropriate permissions to execute transactions and manipulate contract state for testing purposes."},{"instruction":"Ø§ÙƒØªØ¨ Ù…Ù‚Ø§Ù„ Ø¹Ù† ØªØ³Ø±ÙŠØ­Ø© Ø§Ø·ÙØ§Ù„ Ø´Ø¹Ø± Ù‚ØµÙŠØ±","output":"ØªØ³Ø±ÙŠØ­Ø© Ø´Ø¹Ø± Ø§Ù„Ø£Ø·ÙØ§Ù„ Ø§Ù„Ù‚ØµÙŠØ±Ø©: ØªÙ„Ùƒ Ø§Ù„ØªØ³Ø±ÙŠØ­Ø§Øª Ø§Ù„Ø±Ø§Ø¦Ø¹Ø© Ø§Ù„ØªÙŠ ØªØ¶ÙŠÙ Ù„Ù…Ø³Ø© Ù…Ù…ÙŠØ²Ø© Ø¥Ù„Ù‰ Ù…Ø¸Ù‡Ø± Ø§Ù„Ø£Ø·ÙØ§Ù„ Ø§Ù„ØµØºØ§Ø±. ÙÙ‡ÙŠ Ù„ÙŠØ³Øª ÙÙ‚Ø· Ø³Ù‡Ù„Ø© ÙˆÙ…Ø±ÙŠØ­Ø© Ù„Ù„Ø¹Ù†Ø§ÙŠØ© Ø¨Ù‡Ø§ØŒ Ø¨Ù„ ØªØ¶ÙÙŠ Ø£ÙŠØ¶Ù‹Ø§ Ù„Ù…Ø³Ø© Ø£Ù†Ø§Ù‚Ø© Ø¹Ù„Ù‰ Ø§Ù„Ø´Ø¹Ø± Ø§Ù„Ù‚ØµÙŠØ±. ØªØ¹Ø·ÙŠ Ù‡Ø°Ù‡ Ø§Ù„ØªØ³Ø±ÙŠØ­Ø§Øª Ø§Ù„Ø´Ø¨Ù‡ Ø§Ù„Ø¨Ø±ÙŠØ¦Ø© ÙˆØ§Ù„Ù„Ø·ÙŠÙØ© Ù…Ø¸Ù‡Ø±Ù‹Ø§ Ø³Ø§Ø­Ø±Ù‹Ø§ Ù„Ù„Ø£Ø·ÙØ§Ù„ØŒ Ù…Ù…Ø§ ÙŠÙ…Ù†Ø­Ù‡Ù… Ø«Ù‚Ø© ÙÙŠ Ø£Ù†ÙØ³Ù‡Ù… ÙˆØ¬Ø§Ø°Ø¨ÙŠØ©. Ø¥Ù„ÙŠÙƒÙ… Ø¨Ø¹Ø¶ Ø§Ù„ØªØ³Ø±ÙŠØ­Ø§Øª Ø§Ù„Ø´Ø¹Ø± Ø§Ù„Ù‚ØµÙŠØ± Ø§Ù„Ù…ÙØ¶Ù„Ø© Ù„Ø¯Ù‰ Ø§Ù„Ø£Ø·ÙØ§Ù„: 1. \\"Ù‚ØµØ© Ø§Ù„Ø¨ÙƒØ³\\": ØªØ¹Ø¯ Ù‡Ø°Ù‡ Ø§Ù„ØªØ³Ø±ÙŠØ­Ø© Ù…Ù† Ø¨ÙŠÙ† Ø§Ù„ØªØ³Ø±ÙŠØ­Ø§Øª Ø§Ù„Ø£ÙƒØ«Ø± Ø´Ù‡Ø±Ø© Ù„Ù„Ø£Ø·ÙØ§Ù„ Ø§Ù„Ø°ÙŠÙ† ÙŠØ­Ø¨ÙˆÙ† Ø§Ù„Ø­Ø±ÙƒØ© ÙˆØ§Ù„Ù„Ø¹Ø¨. ÙÙ‡ÙŠ ØªØ¹Ø·ÙŠÙ‡Ù… Ù…Ø¸Ù‡Ø±Ù‹Ø§ Ù…Ø±Ø­Ù‹Ø§